import styled from 'styled-components';

export const StyledHeader = styled.div`
    flex: 1 1 auto;
    mcol-gap: 25px;
    background-color: ${(props => props.color)};
    color: #fff;
`;

export const Wrapper = styled.div`
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
`;
