import React, { useState } from 'react';
import CardList from '../card/cards-list.component';
import { members } from './../../config/config';
import { StyledList, Wrapper, StyledHeader, StyledButton } from './styles';
import Modal from 'react-modal';

const Header = () => {

	const customStyles = {
		content: {
			top: '50%',
			left: '50%',
			right: 'auto',
			bottom: 'auto',
			marginRight: '-50%',
			transform: 'translate(-50%, -50%)',
		},
	};


	const [modalIsOpen, setIsOpen] = useState(false);
	const [activeMember, setActiveMember] = useState("");

	const closeModal = () => {
		setIsOpen(false);
	}

	const openModal = (index: number) => {
		setIsOpen(true);
		setActiveMember(members[index] && members[index].name);
	}

	const handleAddList = (index: number) => {
		console.log('list added to', index);
		openModal(index);
	}

	return (<Wrapper>
		{members.map((member, index: number = 0) => {
			return <StyledList>
				<StyledHeader color={member.hexCode}>{member.name}</StyledHeader>
				<CardList cardsInfo={member} colIndex={index}></CardList>
				<StyledButton onClick={() => handleAddList(index)}>Add a card</StyledButton>
			</StyledList>
		})}
		<Modal
			isOpen={modalIsOpen}
			onRequestClose={closeModal}
			contentLabel="Example Modal"
			style={customStyles}
		>
			<h2>`Add a card in team {activeMember}`</h2>
			<button onClick={closeModal}>close</button>
			<div>I am a modal</div>
			<form>
				<input />
				<button>tab navigation</button>
				<button>stays</button>
				<button>inside</button>
				<button>the modal</button>
			</form>
		</Modal>
	</Wrapper>);
}

export default Header;
