import styled from 'styled-components';

export const StyledHeader = styled.div`
    display: inline-flex;
    flex-wrap: wrap;
    gap: 25px;
    background-color: ${(props => props.color)};
    color: #fff;
`;

export const Wrapper = styled.div`
display: flex;
flex-wrap: wrap;
flex-direction: row;
justify-content: space-evenly;
`;
